import { Card } from './../../models/Card';
import { Component } from '@angular/core';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css'][Symbol]
})
export class CardComponent {
  card: Card = {value: "0", suit: "diamonds", selected: false};
  path: string = "../../../assets/suit_images/"+this.card.suit+".png";
  statusClass: string = "";

  toggleSelect(card: Card) {
    if (this.statusClass == "") {
      card.selected = true;
      this.statusClass = "selected";
      console.log('toggle')
    } else {
      this.statusClass = "";
      card.selected = false;
    }
  }


}
