import { Card } from './../../models/Card';
import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent implements OnInit{

  @Input() inputCard!:Card;

  // path: string = "../../../assets/suit_images/"+this.suit+".png";
  path:string='';
  statusClass: string = ""; // used for highlighting cards on hove

  ngOnInit(): void {
    this.path = "../../../assets/suit_images/"+this.inputCard.suit+".png";
  }

  toggleSelect(card: Card) {
    if (this.statusClass == "") {
      card.selected = true;
      this.statusClass = "selected";
    } else {
      this.statusClass = "";
      card.selected = false;
    }
  }


}
<div [ngClass]="statusClass" (click)="toggleSelect(this.inputCard)" id="card-display">
  <h1 id="top-value">{{ this.inputCard.value }}</h1>
  <img src="{{path}}" width="50%">
  <h1 id="bottom-value">{{ this.inputCard.value }}</h1>
</div>
